# a little helper monkeypatch to string
class String
  def /(pieces)
    case pieces
    when Fixnum
      self.chars.each_slice((self.length.to_f / pieces).ceil).to_a.map(&:join)
    else
      raise ArgumentError, "#{pieces} is not a Fixnum"
    end
  end
end

