  def test_match_values_conform_to_rails_internals
    st = "some text. http://peter:password@hello.long.domain.example.mil.uk:8080/some/path?asd=5&asdfb=afd#somewhere and here."
    scheme = href = ''
    st.gsub(ActionView::Helpers::TextHelper::AUTO_LINK_RE) do
      scheme, href = $1, $&
    end
    assert_equal 'http:', scheme
    assert_equal "http://peter:password@hello.long.domain.example.mil.uk:8080/some/path?asd=5&asdfb=afd#somewhere", href
  end

  def test_weird_url
puts ActionView::Helpers::TextHelper::AUTO_LINK_RE.match("<a href=\"http://domain.tld/&amp;title=The%20title%20of%20a%20post\">http://domain.tld/&amp;title=The%20title%20of%20a%20post</a>").inspect
    assert_equal "<a href=\"http://domain.tld/&amp;title=The%20title%20of%20a%20post\">http://domain.tld/&amp;title=The%20title%20of%20a%20post</a>", @helper.auto_link("http://domain.tld/&title=The%20title%20of%20a%20post")
  end

